#!/usr/bin/env bash

set -euo pipefail

id lordvadr && exit 0

useradd -m -U -G wheel lordvadr

mkdir ~lordvadr/.ssh

cat << EOF > ~lordvadr/.ssh/authorized_keys
ssh-rsa  cardno:5021542
ssh-rsa  lordvadr@grissom
EOF

chown -R lordvadr:lordvadr ~lordvadr
chmod 700 ~lordvadr/.ssh
chmod 644 ~lordvadr/.ssh/authorized_keys
